mapper.select:
SELECT	prod_no, prod_name, prod_detail, prod_regdate, prod_price, prod_image
FROM product
WHERE prod_no = #{value}

mapper.update:
UPDATE Product
prod_name = #{prodName}, prod_image = #{prodImage} , expiry_date = #{expiryDate}, prod_detail = #{prodDetail} , prod_price = #{prodPrice}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.*, t.tran_code FROM product p, transaction t
p.prod_no = t.prod_no(+)
AND p.prod_name like '%${searchKeyword}%'
AND p.prod_no = #{searchKeyword}
AND p.prod_price = #{searchKeyword}
ORDER BY p.prod_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM ( SELECT p.prod_no as prod_no, p.prod_name as prod_name, p.prod_detail as prod_detail, p.expiry_date as expiry_date, p.prod_price as prod_price, p.prod_image as prod_image, p.prod_regdate as prod_regdate, t.tran_code as tran_code FROM product p, transaction t
p.prod_no = t.prod_no(+)
AND p.prod_name = #{searchKeyword}
AND p.prod_no like #{searchKeyword}
AND p.prod_price = #{searchKeyword}
) countTable
