mapper.update:
UPDATE screencontent
screen_date = TO_DATE(#{screenDate}, 'YYYY/MM/DD'), screen_opentime = TO_DATE(#{screenOpenTime},'YYYY/MM/DD HH24:MI'), screen_endtime = TO_DATE(#{screenEndTime},'YYYY/MM/DD HH24:MI'), screen_theater = #{screenTheater}, ticket_price = #{ticketPrice}, preview_flag = #{previewFlag}, preview_title = #{previewTitle}, ticket_opendate = TO_DATE(#{ticketOpenDate}, 'YYYY/MM/DD HH24:MI:SS'), invite_actor = #{inviteActor} preview_flag = #{previewFlag}, preview_title = NULL, ticket_opendate = NULL, invite_actor = NULL
WHERE screencontent_no = #{screenContentNo}

mapper.select:
SELECT  TO_CHAR(screen_opentime,'YYYY/MM/DD HH24:MI') screen_opentime, TO_CHAR(screen_endtime,'YYYY/MM/DD HH24:MI') screen_endtime 
FROM	screencontent WHERE screen_theater = #{screenTheater} AND screen_date = #{screenDate}
ORDER BY screen_opentime
